jQuery(function($) {
	document.addEventListener('shopify:block:select', function(event) {
		const block = $(event.target);
		// Testimonial Blocks
	  if ( block.hasClass('thb-testimonial')) {

			const index = block.data('slick-index'),
						slider = block.parents('.thb-testimonials'),
						dots = slider.find('.slick-dots li');
			if (dots) {
				dots.eq(index).trigger('click');
			}
		}
		// HotSpot Slideshow Blocks
	  if ( block.hasClass('product')) {
			const index = block.data('slick-index'),
						slider = block.parents('.thb-hotspots-products-carousel'),
						dots = slider.find('.slick-dots li');
			if (dots) {
				dots.eq(index).trigger('click');
			}
		}

	});
});
